import React, { useState, useEffect } from "react";
import axios from "axios";
import { Card, Button, Container } from 'react-bootstrap';
import "./shop.css";

export const Shop = () => {
  const [loading, setLoading] = useState(true);
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:4200/Producto/');
        setProducts(response.data);
        setLoading(false);
      } catch (error) {
        console.error(error);
        setError(error.message);
        setLoading(false);
      }
    }
    fetchProducts();
  }, []);

  if (loading) {
    return <div className="shop">Loading...</div>;
  }

  if (error) {
    return <div className="shop">Error: {error}</div>;
  }

  if (products.length === 0) {
    return (
      <div className="shop">
        <div className="Title">
          <h1>Lunatiks bikes</h1>
        </div>
        <div className="no-products">No products available.</div>
      </div>
    );
  }

  return (
    <div className="shop">
      <div className="shopTitle">
        <h1>Lunatiks bikes</h1>
      </div>
      <Container fluid style={{ 
        display: 'flex', 
        flexDirection: 'row', 
        justifyContent: 'center', 
        flexWrap: 'wrap',
        width: '100%', 
        height: '100%', 
        margin: '10px'
      }}>
        {products.map((product, index) => (
          <Card key={index} style={{ width: '18rem', margin: '0 20px' }}>
            <Card.Img variant="top" src="img.jpg" />
            <Card.Body>
              <Card.Title>{product.nombre}</Card.Title>
              <Card.Text>{product.descripcion}</Card.Text>
              <Card.Text>{product.precio}</Card.Text>
              <Button variant="primary">Go somewhere</Button>
            </Card.Body>
          </Card>
        ))}
      </Container>
    </div>
  );
};
